//libraries
import React, {Component} from 'react';
import classes from "./Slider.scss";

// import data from './data';

import SliderItem from "./SliderItem/SliderItem";

const data = [
  {
    "id": 1,
    "imgUrl": '../../assets/images/banner1.jpg',
  },
  {
    "id": 2,
    "imgUrl": '../../assets/images/banner2.jpg',
  }, 
  {
    "id": 3,
    "imgUrl":'../../assets/images/banner3.jpg',
  }, 
  {
    "id": 4,
    "imgUrl": '../../assets/images/banner4.jpg',
  }, 
  {
    "id": 5,
    "imgUrl": '../../assets/images/banner5.jpg',
  }, 
]


class Slider extends Component {
  state = {
    'number': 1,
  }

  componentDidMount() {
    this.startInterval()
  }

  startInterval = () => {
    this.interval = setInterval(() => this.changeSlide(), 5000);
  }

   changeSlide = () => {
     let number = this.state.number
     number ++
    if (number > 5) {
      number = 1
    }
    this.setState({
      number: number
    })
  }


  render() {

    return (
      <section className={classes.Slider}>
        <ul className={classes.SliderList} >
          {data.map((index) => { 
            let background
            if (this.state.number === index.id) {
                background = true
              }
              else {
                background = false
              }
            return (
              <SliderItem 
                imgUrl={index.imgUrl} 
                key = {index.id}  
                background = {background}
              />
              )
             }
            )
          }
        </ul>
    </section>
    )
  }
}


export default Slider;
